import { useLocation } from "react-router-dom"; // Import useLocation
import { Box, Typography, LinearProgress } from "@mui/material";

const Pump = () => {
  const location = useLocation(); // Get the location object
  const { member } = location.state || {}; // Destructure `member` from state, fallback to empty object if undefined

  // Function to calculate the amp percentage (assuming max amps is 20)
  const getAmpPercentage = (amp) => {
    const maxAmp = 20; // Define the maximum amps for normalization
    return (amp / maxAmp) * 100;
  };

  return (
    <Box p="20px">
      {member ? (
        <>
          <Typography variant="h4">Pump Information</Typography>
          <Typography variant="body1">
            <strong>Name:</strong> {member.name} <br />
            <strong>ID:</strong> {member.ID} <br />
            <strong>Type:</strong> {member.Type} <br />
            <strong>Status:</strong> {member.Status} <br />
            <strong>Data:</strong> {member.Data} <br />
          </Typography>

          {/* Display the amp meter */}
          <Typography variant="h6" sx={{ marginTop: "20px" }}>
            Amp Meter: {member.amp} A
          </Typography>
          <LinearProgress
            variant="determinate"
            value={getAmpPercentage(member.amp)}
            sx={{
              height: "10px",
              backgroundColor: "lightgray",
              "& .MuiLinearProgress-bar": {
                backgroundColor: member.amp > 15 ? "red" : "green", // Change color if amp is high
              },
            }}
          />
        </>
      ) : (
        <Typography variant="h6">No pump data available.</Typography>
      )}
    </Box>
  );
};

export default Pump;
