import { Box, Button } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import Header from "../../components/Header";
import { useEffect, useState } from "react";

const Form = () => {
  const isNonMobile = useMediaQuery("(min-width:600px)");

  // Initial state for circle properties
  const initialCircleProps = [
    { color: "red", blink: true, text: "Fuel alarm" },
    { color: "green", blink: false, text: "Pressure alarm" },
    { color: "red", blink: true, text: "Water alarm" },
    { color: "green", blink: false, text: "Depth alarm" },
    { color: "red", blink: true, text: "Temperature alarm" },
    { color: "green", blink: false, text: "Water alarm" },
    { color: "red", blink: true, text: "Bilge alarm" },
    { color: "green", blink: false, text: "Fire alarm" },
  ];

  // State to manage circle properties
  const [circleProps, setCircleProps] = useState(initialCircleProps);

  const handleActionButtonClick = () => {
    // Update Circle 3 to be green and not blinking
    setCircleProps((prevProps) => {
      const newProps = [...prevProps];
      newProps[0] = { color: "green", blink: false, text: "Fuel alarm" };
      newProps[1] = { color: "green", blink: false, text: "Pressure alarm" };
      newProps[2] = { color: "green", blink: false, text: "Water alarm" };
      newProps[3] = { color: "green", blink: false, text: "Depth alarm"  };
      newProps[4] = { color: "green", blink: false, text: "Temperature alarm" };
      newProps[5] = { color: "green", blink: false , text: "Water alarm"};
      newProps[6] = { color: "green", blink: false, text: "Bilge alarm" };
      newProps[7] = { color: "green", blink: false, text: "Fire alarm" };
      return newProps;
      
    });
  };

  return (
    <Box m="20px">
      <Header title="TEXT BOXES" subtitle="Display Text Boxes" />

      <Box
        display="grid"
        gap="30px"
        gridTemplateColumns="repeat(4, 1fr)" // 4 columns
        sx={{
          "& > div": { gridColumn: isNonMobile ? "span 1" : "span 4" },
        }}
      >
        {circleProps.map((circle, index) => (
          <CircleBox key={index} color={circle.color} blink={circle.blink} text={circle.text} />
        ))}
      </Box>
      <Box display="flex" justifyContent="end" mt="20px">
        <Button color="secondary" variant="contained" onClick={handleActionButtonClick}>
          Reset alarms
        </Button>
      </Box>
    </Box>
  );
};

const CircleBox = ({ color, blink, text }) => {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    if (blink) {
      const interval = setInterval(() => {
        setIsVisible((prev) => !prev);
      }, 500); // Blink every 500ms
      return () => clearInterval(interval);
    } else {
      setIsVisible(true); // Ensure the circle is visible if not blinking
    }
  }, [blink]);

  return (
    <Box
      sx={{
        padding: "32px", // Increased padding
        border: "1px solid #ccc",
        borderRadius: "4px",
        textAlign: "center",
        minHeight: "150px", // Increased height for the box
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-end", // Aligns the circle to the bottom
        alignItems: "center",
      }}
    >
      <div>{text}</div> {/* Display individual text */}
      <Box
        sx={{
          width: "40px", // Width of the circle
          height: "40px", // Height of the circle
          borderRadius: "50%", // Makes it a circle
          backgroundColor: color, // Circle color
          mt: "16px", // Margin-top to space it from the text
          opacity: isVisible ? 1 : 0, // Controls visibility for blinking effect
          transition: "opacity 0.5s", // Smooth transition for blinking
        }}
      />
    </Box>
  );
};

export default Form;
