import { Box, Typography, Grid, Container, useTheme } from "@mui/material";
import { tokens } from "../../theme";
import Header from "../../components/Header";

const Dashboard = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);

  // MainComponent content (directly added here)
  const shipInfo = `
    <br/>
    <br/>
    <strong>My ship:</strong><br/>
    IMO: 9612636<br/>
    General vessel type: Cargo<br/>
    MMSI: 245179000<br/>
    Call sign: PCRR<br/>
    Gross Tonnage: 19848<br/>
    Summer DWT: 6500 t<br/>
    Length Overall x Breadth Extreme: 139.4 x 44.4 m
  `;

  const contactInfo = `
    <strong>Contact info:</strong><br/>
    Van der leun<br/>
    info@royalvanderleun.com<br/>
    +31 (0)184 413 288<br/>
    <a href="https://www.vanderleun.nl/nl/" target="_blank" rel="noopener noreferrer"style="color: orange;">Website</a><br/>
    <a href="https://www.instagram.com/royalvanderleun/" target="_blank" rel="noopener noreferrer"style="color: orange;">instagram</a><br/>
    <a href="https://www.facebook.com/bvvanderleun/?locale=nl_NL" target="_blank" rel="noopener noreferrer"style="color: orange;">facebook</a><br/>
  `;

  const locationInfo = `
    <br/>
    <strong>Location:</strong><br/>
    Position received: 2024-01-13 14:10 LT (UTC -5)<br/>
    Area: USEC - US East Coast<br/>
    Navigational Status: Moored<br/>
    Speed/Course: 0 kn / 16 °<br/>
    Reported Destination: SOUTHFORK WINDFARM
  `;

  const formatText = (text) => (
    <div dangerouslySetInnerHTML={{ __html: text }} />
  );

  // Styling for the title box and the blue strip
  const titleBoxStyle = {
    width: '100%',
    height: '60px',
    backgroundColor: '#f36617',
    color: 'white',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '16px',
  };

  const blueStripStyle = {
    bottom: 0,
    left: 0,
    width: '100%',
    height: '60px', // Height of the strip
    backgroundColor: '#1e54ae', // Full-width blue strip
    zIndex: 1000, // Ensure it is above other content
  };

  const BoxComponent = ({ backgroundColor, children }) => (
    <Box
      sx={{
        backgroundColor: backgroundColor,
        width: 400,
        height: 600,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white', // Set text color to white
        fontSize: '24px',
        padding: '10px',
        paddingTop: '0px',
        borderRadius: '30px',
        textAlign: 'center',
        border: '5px solid #f36617',
        margin: '5px',
        boxSizing: 'border-box',
        overflowY: 'auto',
      }}
    >
      {children}
    </Box>
  );

  return (
    <Box m="20px">
      {/* HEADER */}
      <Box display="flex" justifyContent="space-between" alignItems="center">
        <Header title="SmartSwitch" subtitle="Welcome to your SmartSwitch Board!" />
      </Box>

      {/* GRID & CHARTS */}
      <Box display="flex" justifyContent="center" mt="20px">
        {/* MainComponent content directly embedded */}
        <Box sx={titleBoxStyle}>Home</Box>
      </Box>

      <Box sx={{ minHeight: 'calc(100vh - 120px)', paddingBottom: '100px', marginTop: '20px' }}>
        <Container>
          <Grid container spacing={6} justifyContent="center">
            <Grid item xs={12} md={4}>
              <BoxComponent backgroundColor={colors.primary[400]}>
                {formatText(shipInfo)}
              </BoxComponent>
            </Grid>
            <Grid item xs={12} md={4}>
              <BoxComponent backgroundColor={colors.primary[400]}>
                {formatText(locationInfo)}
              </BoxComponent>
            </Grid>
            <Grid item xs={12} md={4}>
              <BoxComponent backgroundColor={colors.primary[400]}>
                {formatText(contactInfo)}
              </BoxComponent>
            </Grid>
          </Grid>
        </Container>
      </Box>

      <Box sx={blueStripStyle} />
    </Box>
  );
};

export default Dashboard;
