import { useLocation } from "react-router-dom"; // Import useLocation
import { Box, Typography, LinearProgress } from "@mui/material";


const AnalogMeter = ({ value, minValue, maxValue }) => {
    // Calculate rotation based on the value
    const range = maxValue - minValue;
    const rotation = ((value - minValue) / range) * 180 - 90; // Needle rotates from -90 to +90 degrees
  
    return (
      <Box display="flex" alignItems="center" justifyContent="center" p="20px">
        <Box position="relative" width="150px" height="75px">
          {/* Background of the meter */}
          <svg width="150" height="75">
            <path
              d="M 10 75 A 65 65 0 0 1 140 75"
              fill="none"
              stroke="#ddd"
              strokeWidth="10"
            />
            <path
              d="M 10 75 A 65 65 0 0 1 140 75"
              fill="none"
              stroke="orange"
              strokeWidth="10"
              strokeDasharray="150, 50" // You can control the length of the orange arc
            />
          </svg>
  
          {/* Needle */}
          <Box
            position="absolute"
            left="50%"
            top="75%"
            width="2px"
            height="70px"
            bgcolor="red"
            transformOrigin="bottom"
            style={{ transform: `rotate(${rotation}deg)` }}
          />
  
          {/* Center Circle */}
          <Box
            position="absolute"
            left="calc(50% - 5px)"
            top="calc(75% - 5px)"
            width="10px"
            height="10px"
            borderRadius="50%"
            bgcolor="gray"
          />
        </Box>
  
        {/* Value display */}
        <Box ml={2}>
          <Typography variant="h6">{value} A</Typography>
        </Box>
      </Box>
    );
  };

const Pump = () => {
  const location = useLocation(); // Get the location object
  const { member } = location.state || {}; // Destructure `member` from state, fallback to empty object if undefined

  // Function to calculate the amp percentage (assuming max amps is 20)
  const getAmpPercentage = (amp) => {
    const maxAmp = 20; // Define the maximum amps for normalization
    return (amp / maxAmp) * 100;
  };

  return (
    <Box p="20px">
      {member ? (
        <>
          <Typography variant="h4">Pump Information</Typography>
          <Typography variant="body1">
            <strong>Name:</strong> {member.name} <br />
            <strong>ID:</strong> {member.ID} <br />
            <strong>Type:</strong> {member.Type} <br />
            <strong>Status:</strong> {member.Status} <br />
            <strong>Data:</strong> {member.Data} <br />
          </Typography>

          {/* Display the amp meter */}
          <Typography variant="h6" sx={{ marginTop: "20px" }}>
            Amp Meter: {member.amp} A
          </Typography>
          <LinearProgress
            variant="determinate"
            value={getAmpPercentage(member.amp)}
            sx={{
              height: "10px",
              backgroundColor: "lightgray",
              "& .MuiLinearProgress-bar": {
                backgroundColor: member.amp > 15 ? "red" : "green", // Change color if amp is high
              },
            }}
          />
        </>
      ) : (
        <Typography variant="h6">No pump data available.</Typography>
      )}
    </Box>
  );
};

export default Pump;
