import { Box, Button } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import Header from "../../components/Header";

const Form = () => {
  const isNonMobile = useMediaQuery("(min-width:600px)");

  return (
    <Box m="20px">
      <Header title="TEXT BOXES" subtitle="Display Text Boxes" />

      <Box
        display="grid"
        gap="30px"
        gridTemplateColumns="repeat(4, 1fr)" // 4 columns
        sx={{
          "& > div": { gridColumn: isNonMobile ? "span 1" : "span 4" },
        }}
      >
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 1
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 2
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 3
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 4
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 5
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 6
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 7
        </Box>
        <Box
          sx={{
            padding: "16px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            textAlign: "center",
          }}
        >
          Text Box 8
        </Box>
      </Box>
      <Box display="flex" justifyContent="end" mt="20px">
        <Button color="secondary" variant="contained">
          Action Button
        </Button>
      </Box>
    </Box>
  );
};

export default Form;
