import { createContext, useState, useMemo } from "react";
import { createTheme } from "@mui/material/styles";

// color design tokens export
export const tokens = (mode) => ({
  ...(mode === "dark"
    ? {
        grey: {
          100: "#e0e0e0",
          200: "#c2c2c2",
          300: "#a3a3a3",
          400: "#858585",
          500: "#666666",
          600: "#525252",
          700: "#3d3d3d",
          800: "#292929",
          900: "#141414",
        },
        primary: {
          100: "#b56d2e", // light royal orange
          200: "#b56d2e", // soft orange
          300: "#b56d2e", // vibrant orange
          400: "#a25f0f", // royal orange
          500: "#8a4f00", // deep orange
          600: "#7a3f00", // darker orange
          700: "#6a3200", // rich dark orange
          800: "#5b2400", // very dark orange
          900: "#4d1800", // darkest burnt orange
        },
        
        blueAccent: {
          100: "#c0d9f0", // light blue
          200: "#8cb8e2", // soft blue
          300: "#64a0d4", // pastel blue
          400: "#0076b4", // vibrant blue
          500: "#005f8d", // royal blue
          600: "#00476d", // deep blue
          700: "#003555", // dark blue
          800: "#002540", // navy blue
          900: "#001720", // midnight blue
        },
        greenAccent: {
          100: "#d0f0c0", // light mint
          200: "#a8e3a0", // soft green
          300: "#80d160", // bright green
          400: "#5ab600", // vivid green
          500: "#4ca700", // dark green
          600: "#3f9600", // rich green
          700: "#368200", // forest green
          800: "#2c6e00", // deep forest
          900: "#225b00", // dark forest
        },
        redAccent: {
          100: "#f8d0d0", // light pink
          200: "#f1a8a8", // soft red
          300: "#e67272", // vibrant red
          400: "#db4f4a", // rich red
          500: "#c52d2d", // dark red
          600: "#b12b2b", // crimson
          700: "#9b2525", // ruby
          800: "#7a1d1d", // garnet
          900: "#5c1212", // dark garnet
        },
      }
    : {
        grey: {
          100: "#141414",
          200: "#292929",
          300: "#3d3d3d",
          400: "#525252",
          500: "#666666",
          600: "#858585",
          700: "#a3a3a3",
          800: "#c2c2c2",
          900: "#e0e0e0",
        },
        primary: {
          100: "#260f28", // blackcurrant
          200: "#311b3a", // eggplant
          300: "#3c234d", // dark violet
          400: "#5a3475", // deep purple
          500: "#7d4c8b", // royal purple
          600: "#b086a1", // muted purple
          700: "#c9a4c5", // soft purple
          800: "#e6d6e9", // light lavender
          900: "#d0d1d5", // light lavender for light mode
        },
        greenAccent: {
          100: "#225b00", // dark forest
          200: "#2c6e00", // deep forest
          300: "#368200", // forest green
          400: "#3f9600", // rich green
          500: "#4ca700", // dark green
          600: "#5ab600", // vivid green
          700: "#80d160", // bright green
          800: "#a8e3a0", // soft green
          900: "#d0f0c0", // light mint
        },
        redAccent: {
          100: "#5c1212", // dark garnet
          200: "#7a1d1d", // garnet
          300: "#9b2525", // ruby
          400: "#b12b2b", // crimson
          500: "#c52d2d", // dark red
          600: "#db4f4a", // rich red
          700: "#e67272", // vibrant red
          800: "#f1a8a8", // soft red
          900: "#f8d0d0", // light pink
        },
        blueAccent: {
          100: "#0a2a40", // midnight blue
          200: "#0f3b55", // navy
          300: "#164c6b", // dark teal
          400: "#1e5782", // deep blue
          500: "#276a9b", // royal blue
          600: "#4d8cb8", // bright blue
          700: "#80b3d0", // pastel blue
          800: "#b3cce0", // soft blue
          900: "#e0ebf2", // light sky blue
        },
      }),
});

// mui theme settings
export const themeSettings = (mode) => {
  const colors = tokens(mode);
  return {
    palette: {
      mode: mode,
      ...(mode === "dark"
        ? {
            primary: {
              main: colors.primary[500],
            },
            secondary: {
              main: colors.greenAccent[500],
            },
            neutral: {
              dark: colors.grey[700],
              main: colors.grey[500],
              light: colors.grey[100],
            },
            background: {
              default: colors.primary[500],
            },
          }
        : {
            primary: {
              main: colors.primary[100],
            },
            secondary: {
              main: colors.greenAccent[500],
            },
            neutral: {
              dark: colors.grey[700],
              main: colors.grey[500],
              light: colors.grey[100],
            },
            background: {
              default: "#fcfcfc",
            },
          }),
    },
    typography: {
      fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
      fontSize: 12,
      h1: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 40,
      },
      h2: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 32,
      },
      h3: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 24,
      },
      h4: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 20,
      },
      h5: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 16,
      },
      h6: {
        fontFamily: ["Source Sans Pro", "sans-serif"].join(","),
        fontSize: 14,
      },
    },
  };
};

// context for color mode
export const ColorModeContext = createContext({
  toggleColorMode: () => {},
});

export const useMode = () => {
  const [mode, setMode] = useState("dark");

  const colorMode = useMemo(
    () => ({
      toggleColorMode: () =>
        setMode((prev) => (prev === "light" ? "dark" : "light")),
    }),
    []
  );

  const theme = useMemo(() => createTheme(themeSettings(mode)), [mode]);
  return [theme, colorMode];
};
