import React from "react";
import { useLocation } from "react-router-dom";
import { Box, Typography, Accordion, AccordionSummary, AccordionDetails, Grid, Button, useTheme } from "@mui/material";
import { RadialGauge } from "react-canvas-gauges"; // Import RadialGauge from the library
import { tokens } from "../../theme";
import { useEffect, useState } from "react";
const Pump = () => {
    const theme = useTheme();
    const colors = tokens(theme.palette.mode);
    const location = useLocation();
    const [isPumping, setIsPumping] = useState(false); 
    const { member } = location.state || {};

    return (
        <Box p="20px">
            {member ? (
                <>
                    <Typography variant="h4">Pump Information</Typography>
                    <Typography variant="body1">
                        <strong>Name:</strong> {member.name} <br />
                        <strong>ID:</strong> {member.ID} <br />
                        <strong>Type:</strong> {member.Type} <br />
                        <strong>Status:</strong> {member.Status} <br />
                        <strong>Data:</strong> {member.Data} <br />
                    </Typography>

                    {/* Display the analog amp meter */}
                    {/* <Typography variant="h6" sx={{ color: "white", marginTop: "20px" }}>
            
          </Typography> */}

                    {/* RadialGauge for analog meter visualization */}
                    <RadialGauge
                        width={300}
                        height={300}
                        units="Kw"
                        minValue={0} // Define the minimum value
                        maxValue={20} // Define the maximum value
                        majorTicks={["0", "5", "10", "15", "20", "25", "30"]} // Major tick marks
                        minorTicks={2} // Minor tick marks between major ticks
                        value={member.amp} // Current amp value
                        colorPlate={colors.grey[600]} // Background color of the gauge
                        colorNeedle="red" // Needle color
                        colorNeedleEnd="orange"
                        colorNeedleCircleOuter="black"
                        colorMajorTicks="White" // Major tick color
                        colorMinorTicks="LightGray" // Minor tick color, if supported  
                        colorUnits="orange"
                        colorOuterring="gray"
                        colorNumbers="White"
                        colorBorderOuter="black"
                        needleCircleSize={15} // Size of the needle's center circle
                        animationRule="linear"
                        animationDuration={1500} // Animation duration for smooth transitions
                    />
                    <Box display="flex" justifyContent="flex-end" mt={2}>
                        <Button
                            variant="contained"
                            size="Big"
                            color={isPumping ? "error" : "blue"} 
                            sx={{ backgroundColor: colors.blueAccent[500] }}
                            onClick={() => setIsPumping((prev) => !prev)}
                        >
                            {isPumping ? "Stop Pump " : "Run Pump "}
                        </Button>
                    </Box>
                </>
            ) : (
                <Typography variant="h6">No pump data available.</Typography>
            )}
        </Box>
    );
};

export default Pump;
