import React, { useState } from 'react';
import { Accordion, AccordionSummary, AccordionDetails, Checkbox, Typography, Box } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const AccordionWithCheckboxes = () => {
  const [checkedItems, setCheckedItems] = useState({
    section1: false,
    section2: false,
    section3: false,
  });

  const handleCheckboxChange = (section) => {
    setCheckedItems({
      ...checkedItems,
      [section]: !checkedItems[section],
    });
  };

  return (
    <Box>
      {/* Accordion 1 */}
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>Section 1</Typography>
          <Checkbox
            checked={checkedItems.section1}
            onChange={() => handleCheckboxChange('section1')}
            inputProps={{ 'aria-label': 'Checkbox for section 1' }}
          />
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Content for section 1
          </Typography>
        </AccordionDetails>
      </Accordion>

      {/* Accordion 2 */}
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>Section 2</Typography>
          <Checkbox
            checked={checkedItems.section2}
            onChange={() => handleCheckboxChange('section2')}
            inputProps={{ 'aria-label': 'Checkbox for section 2' }}
          />
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Content for section 2
          </Typography>
        </AccordionDetails>
      </Accordion>

      {/* Accordion 3 */}
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>Section 3</Typography>
          <Checkbox
            checked={checkedItems.section3}
            onChange={() => handleCheckboxChange('section3')}
            inputProps={{ 'aria-label': 'Checkbox for section 3' }}
          />
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Content for section 3
          </Typography>
        </AccordionDetails>
      </Accordion>
    </Box>
  );
};

export default AccordionWithCheckboxes;
