import { Box, Typography, Accordion, AccordionSummary, AccordionDetails, Grid, Button, useTheme } from "@mui/material";
import ExpandMoreOutlinedIcon from "@mui/icons-material/ExpandMoreOutlined";
import { tokens } from "../../theme";
import { mockDataTeam } from "../../data/mockData";
import Header from "../../components/Header";
import { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const Team = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  const [expandedId, setExpandedId] = useState(null); // State to manage expanded accordion
  const [showNext, setShowNext] = useState({}); // State to manage next section visibility
  const [buttonStatus, setButtonStatus] = useState({}); // State to manage button statuses
  const navigate = useNavigate(); // Initialize useNavigate for navigation

  const handleNext = (memberId) => {
    setShowNext((prev) => ({
      ...prev,
      [memberId]: !prev[memberId], // Toggle visibility for the clicked member
    }));
  };

  const toggleButtonStatus = (memberId) => {
    setButtonStatus((prev) => ({
      ...prev,
      [memberId]: prev[memberId] === "On" ? "Off" : "On", // Toggle button status
    }));
  };

  const goToPumpPage = () => {
    navigate("/pump"); // Navigate to Pump page when button is clicked
  };

  return (
    <Box m="20px">
      <Header title="SENSOR DATA" subtitle="Data from misc sources" />
      <Grid container spacing={2} sx={{ marginTop: "40px" }}>
        {mockDataTeam.map((member) => (
          <Grid item xs={12} sm={6} md={4} key={member.id}>
            <Accordion 
              sx={{ backgroundColor: colors.primary[400] }} 
              expanded={expandedId === member.id} 
              onChange={() => setExpandedId(expandedId === member.id ? null : member.id)} // Toggle accordion
            >
              <AccordionSummary
                expandIcon={<ExpandMoreOutlinedIcon />}
                sx={{ backgroundColor: colors.blueAccent[700] }}
              >
                <Typography variant="h6" sx={{ color: colors.grey[100], flexGrow: 1 }}>
                  {member.name} (ID: {member.ID})
                </Typography>
              </AccordionSummary>
              <AccordionDetails sx={{ backgroundColor: colors.primary[400] }}>
                <Typography sx={{ color: colors.grey[100] }}>
                  <strong>Type:</strong> {member.Type}<br />
                  <strong>Status:</strong> {member.Status}<br />
                  <strong>Data:</strong> {member.Data}<br />
                </Typography>
                <Box display="flex" justifyContent="flex-end" mt={2}>
                  <Button 
                    variant="contained" 
                    size="small" 
                    sx={{ backgroundColor: colors.blueAccent[500], marginRight: '8px' }} 
                    onClick={toggleButtonStatus} // Call toggleButtonStatus on click
                  >
                    {buttonStatus[member.id] || "On"} {/* Display "On" or "Off" */}
                  </Button>
                  <Button 
                    variant="contained" 
                    size="small" 
                    sx={{ backgroundColor: colors.greenAccent[500] }} 
                    onClick={goToPumpPage} // Navigate to Pump page
                  >
                    Go to Pump
                  </Button>
                </Box>
                {showNext[member.id] && ( // Conditionally render the next section
                  <Box sx={{ marginTop: 2, backgroundColor: colors.grey[800], padding: 2, borderRadius: 2 }}>
                    <Typography variant="h6" sx={{ color: colors.grey[100] }}>
                      Additional Information for {member.name}
                    </Typography>
                    <Typography sx={{ color: colors.grey[300] }}>
                      {/* Add more details here for the member */}
                      Here you can add any additional content or details related to {member.name}.
                    </Typography>
                  </Box>
                )}
              </AccordionDetails>
            </Accordion>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Team;
